import React from "react";
import "./HorizontalCard.css";

const HorizontalCard = ({
  idx,
  imgSrc,
  altText,
  heading,
  subheading,
  classes,
}) => {
  return (
    <div
      className={`flex justify-between ${
        idx % 2 === 0 ? "row-reverse" : ""
      } ${classes}`}
    >
      <div style={{ overflow: "hidden", maxWidth: "100%", height: auto }}>
        <img src={imgSrc} alt={altText} />
      </div>
      <div>
        <h1>{heading}</h1>
        <h3>{subheading}</h3>
      </div>
    </div>
  );
};

export default HorizontalCard;
